import { deleteMenuFailure, deleteMenuStart, deleteMenuSuccess, getMenuFailure, getMenuStart, getMenuSuccess } from "./MenuActions"
import axios from "axios"

export const getMenu = async (dispatch) =>{

    dispatch(getMenuStart())
    try {
        const res = await axios.get("/menus/", {
            Headers: {
                token: "Bearer " + JSON.parse(localStorage.getItem("user")).accessToken,
            },
        });

        dispatch(getMenuSuccess(res.data))    
    } catch (err) {
        console.log(err)
        dispatch(getMenuFailure());
    }
} 


export const deleteMenu = async (id,dispatch) =>{

    dispatch(deleteMenuStart())
    try {
        console.log("id",id)
        await axios.delete("menus/"+id, {
            Headers: {
                token: "Bearer " + JSON.parse(localStorage.getItem("user")).accessToken,
            },
        });

        dispatch(deleteMenuSuccess(id))    
    } catch (err) {
        console.log("masok",err)
        dispatch(deleteMenuFailure());
    }
}
