import Sidebar from "./components/sidebar/Sidebar";
import Topbar from "./components/topbar/Topbar";
import "./App.css";
import Home from "./pages/home/Home";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import UserList from "./pages/userList/UserList";
import User from "./pages/user/User";
import NewUser from "./pages/newUser/NewUser";
import ProductList from "./pages/productList/ProductList";
import Product from "./pages/product/Product";
import NewProduct from "./pages/newProduct/NewProduct";
import { useState } from "react";
import { useEffect } from "react";
import axiox from "axios";
import { useMemo } from "react";


function App() {
  const monthsArray= useMemo(()=>[
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December"],[]);

    const [userStats, setUserStatas] = useState([]);

    useEffect(()=>{
      const getStats = async () =>{
        try {
          const res = await axiox.get("/users/stats", {
            headers: {
              token: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxODM3ZDQyYTVjMmE0ODMzZDE0ZTM5OSIsInJvbGUiOnRydWUsImlhdCI6MTYzOTU1MTUwOSwiZXhwIjoxNjM5OTgzNTA5fQ.uArFYtu3A2rSsA3zAUV1xYm7F9ZK8mArCd-3GAgLfnM"
            }
          });
          res.data.map(item => setUserStatas(prev=>[...prev,{name: monthsArray[item._id-1], "New User": item.total}]))
        } catch (err) {
          console.log(err)
        }
      }
      getStats();
    },[monthsArray]);

    console.log(userStats);


  return (
    <Router>
      <Topbar />
      <div className="container">
        <Sidebar />
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/users">
            <UserList />
          </Route>
          <Route path="/user/:userId">
            <User />
          </Route>
          <Route path="/newUser">
            <NewUser />
          </Route>
          <Route path="/products">
            <ProductList />
          </Route>
          <Route path="/product/:productId">
            <Product />
          </Route>
          <Route path="/newproduct">
            <NewProduct />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
