import React, { useState } from 'react'
import Fab from '@material-ui/core/Fab'
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart'
import Badge from '@material-ui/core/Badge'
import NumberFormat from 'react-number-format'

import './cart.scss'
import { useDispatch, useSelector } from 'react-redux'
import { addItem, removeItem, selectCartCount, selectCartItem, selectTotalOrder } from '../../../../redux/cartSlice'
import { selectRestoMenu } from '../../../../redux/restoSlice'
import { CardMenuMini } from '../..';
import { Button, ButtonGroup, Dialog, FormControl, InputLabel, MenuItem, Select } from '@material-ui/core';



const Cart = () => {
    const count = useSelector(selectCartCount);
    const items  = useSelector(selectCartItem);
    const menu = useSelector(selectRestoMenu);
    const totalOrder = useSelector(selectTotalOrder);

    const dispatch = useDispatch();

    //loadRestoAsync();

    // const handleCartClick = (e) =>{
    //     e.preventDefault();
    //     if (count === 0 ) return; //cek cart kosong apa tidak
    //     console.log(",asuk")
    //     Object.values(items).reduce((acc) =>{
    //       console.log("masuk",acc);
    //         return acc
    //     });
    //     // console.log("total: ", total)
    // };

  // getModalStyle is not a pure function, we roll the style only on the first render

  const [open, setOpen] = useState(false);
  // const checkOut = {
  //   table_id: "TB-02",
  //   NameOrder: "surya",
  //   order: Object.values(items),
  //   totalOrder: totalOrder,
  //   qtyItem: count,

  // };

  const handleChange = (e ) => {
    //e.preventDefault();
    const {name , value} = e.target 
    //console.log(e.target)  
    setState(prevState => ({
        ...prevState,
        [name] : value
    }))  
  }


  const handleOpen = () => {
    if (count !== 0 )
    if (open === true) return setOpen(false) 
    return setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  console.log( "total", totalOrder );
  const body = (
    <div className="papper">
        <h3>Your Order</h3>
        {items !== undefined ?
          <div>
            {
              Object.values(items).map((data, index) => (

                <div className="detail-cart" key={index}>
                    <div className="product">
                          <CardMenuMini image={menu.find(x => x.food_id === data.food_id).img}/>
                    </div>
                    <div className="title-product">
                        <p>{menu.find(x => x.food_id === data.food_id).title}</p>
                    </div>
                    <div className="qty-custom">
                    <ButtonGroup size="small" aria-label="small outlined button group">

                    <Button onClick={(e) => {
                       e.preventDefault();
                       dispatch(removeItem({food_id: data.food_id, price: menu.find(x => x.food_id === data.food_id).price}));
                      }}>-</Button>

                      <Button disabled>{data.qty}</Button>

                      <Button onClick={(e)=> {
                        e.preventDefault();
                        dispatch(addItem({food_id: data.food_id, price: menu.find(x => x.food_id === data.food_id).price}));
                      }}>+</Button>



                      </ButtonGroup>
                    </div>
                </div>
              ))
            }
          </div>
          :
          ''
        }
      <div className="detail-checkout">
        <div><p>Qty: </p></div>
        <div><NumberFormat 
                    className="price"
                    value={count}
                    displayType="text"
                    thousandSeparator={true}
                    suffix=' item'
                /></div>

      </div>
      <div className="detail-checkout">
        <div><p>Estimate Price: </p></div>
        <div><NumberFormat 
                    className="price"
                    value={totalOrder}
                    displayType="text"
                    thousandSeparator={true}
                    prefix="Rp "
                /></div>

      </div>
      <div className="detail-checkout">
                        <InputLabel className="from-title">Table:</InputLabel>
                        <FormControl className="from-input">
                            <InputLabel id="categories">enter you table number</InputLabel>
                            <Select
                            name="categories"
                            id="categories"
                            onChange={handleChange}
                            value={''}
                            >
                            <MenuItem value={'TB-1'}>T-1</MenuItem>
                            <MenuItem value={'TB-2'}>T-2</MenuItem>
                            <MenuItem value={'TB-3'}>T-3</MenuItem>
                            <MenuItem value={'TB-4'}>T-4</MenuItem>
                            <MenuItem value={'TB-5'}>T-5</MenuItem>
                            <MenuItem value={'TB-6'}>T-6</MenuItem>
                            <MenuItem value={'TB-7'}>T-7</MenuItem>
                            </Select>
                        </FormControl>
                    </div>
      <div className="detail-cart">
        <Button fullWidth={true} color="primary" variant="contained">checkout</Button>  
      </div>
      <Cart />
    </div>
  );

    //setTimeout(() =>{console.log(menu)},2000)
    return (
        <div>
            <Fab className={ count !== 0 ? "fab" : undefined } color="primary" aria-label="add" onClick={handleOpen}>
                <Badge badgeContent={count} color="error">
                    <ShoppingCartIcon />
                </Badge>
            </Fab>
            {/* <Modal
                open={open}
                onClose={handleClose}
              > */}
                  <Dialog 
                    open={open}
                    onClose={handleClose}
                    scroll="body"
                    fullWidth={true}
                    //fullScreen={true}

                  >{body}</Dialog>

            {/* </Modal> */}
        </div>
    )
}

export default Cart
