import { ButtonBase } from '@material-ui/core'
import React from 'react'
import NumberFormat from 'react-number-format'
import { connect } from 'react-redux'
//import { foodimg} from '../../../../assets'
import './cardMenu.scss'


const CardMenu = ({title, image, price, mapDispatchToProps, mapStateToProps}) => {
    console.log("DATA :",mapStateToProps)
    return (
        <div className="container-cardmenu">
            <div className="img-cover" >
                <img className="img-cardmenu" src={image} alt="foodimg"/>
            </div>
            <p className="title-menu">{title}</p>
            {price ? 
            <div className="price-checkout">
                <NumberFormat 
                    className="price"
                    value={price}
                    displayType="text"
                    thousandSeparator={true}
                    prefix="Rp "
                />
                <ButtonBase className="checkout">add item</ButtonBase> 
            </div>
            : ''}
        </div>
    )
}

const mapDispatchToProps = (dispatch) => {
    return{
        handlePlus: () => dispatch('PLUS_ORDER')
    }
}
const mapStateToProps = (state) => {
    return state
}

export default connect(mapStateToProps, mapDispatchToProps)(CardMenu)
