import React, { useState } from 'react'
import Fab from '@material-ui/core/Fab'
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart'
import Badge from '@material-ui/core/Badge'
//import Modal from '@material-ui/core/Modal';

import './cart.scss'
import { useSelector } from 'react-redux'
import { selectCartCount, selectCartItem } from '../../../../redux/cartSlice'
import {  selectRestoMenu } from '../../../../redux/restoSlice'
import { CardMenuMini } from '../..';
import { Button, ButtonGroup, Dialog } from '@material-ui/core';



const Cart = () => {
    const count = useSelector(selectCartCount);
    const items  = [useSelector(selectCartItem)];
    const menu = useSelector(selectRestoMenu);

    //loadRestoAsync();

    const handleCartClick = (e) =>{
        e.preventDefault();
        if (count === 0 ) return console.log("cart Kosong bego"); //cek cart kosong apa tidak

        const total = Object.values(items).reduce((acc, item) =>{
            const menuItem = menu[item.food_id];
            if( menuItem === undefined) return acc ;

            return acc + menuItem.price * item.qty;;
        });
        console.log("total: ", total)
    };

  // getModalStyle is not a pure function, we roll the style only on the first render

  const [open, setOpen] = useState(false);
  const [counter, setCounter] = useState(0);

  //console.log( items )
  const body = (
    <div className="papper">
        <h3>Your Order</h3>
        {items !== undefined ?
          <div>
            {
              items.map((data, index) => (
                <div className="detail-cart" key={index}>
                    <div className="product">
                          <CardMenuMini image={data.img}/>
                    </div>
                    <div className="title-product">
                        <p>{data.title}</p>
                    </div>
                    <div className="qty-custom">
                    <ButtonGroup size="small" aria-label="small outlined button group">

                      <Button onClick={()=> 
                      {setCounter(counter+1)}}>+</Button>

                      {<Button disabled>{counter}</Button>}

                      {<Button disabled={counter <= 0} onClick={() => {
                      setCounter(counter - 1)
                      }}>-</Button>}

                      </ButtonGroup>
                    </div>
                </div>
              ))
            }
          </div>
          :
          ''
        }

      <Cart />
    </div>
  );

  const handleOpen = () => {
    if (count !== 0 )
    if (open === true) return setOpen(false) 
    return setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


    //setTimeout(() =>{console.log(menu)},2000)
    return (
        <div>
            <Fab className="fab" color="primary" aria-label="add" onClick={handleCartClick}>
                <Badge badgeContent={count} color="error">
                    <ShoppingCartIcon />
                </Badge>
            </Fab>
            {/* <Modal
                open={open}
                onClose={handleClose}
              > */}
                  <Dialog 
                    open={open}
                    onClose={handleClose}
                    scroll="body"
                    fullWidth={true}
                    //fullScreen={true}

                  >{body}</Dialog>

            {/* </Modal> */}
        </div>
    )
}

export default Cart
